-- Tạo bảng roles
CREATE TABLE UTC_JAVA.roles (
                                id NUMBER(10) GENERATED BY DEFAULT AS IDENTITY,
                                name VARCHAR2(64) NOT NULL,
                                status NUMBER(1) NOT NULL,
                                created_date DATE NOT NULL,
                                modified_by VARCHAR2(64) NOT NULL,
                                PRIMARY KEY (id)
);
GRANT ALL PRIVILEGES ON UTC_JAVA.ROLES TO UTC_JAVA;
-- Tạo bảng users
CREATE TABLE UTC_JAVA.users (
                                id NUMBER(19) GENERATED BY DEFAULT AS IDENTITY,
                                full_name VARCHAR2(64) NULL,
                                avatar VARCHAR2(255) NULL,
                                address VARCHAR2(255) NULL,
                                phone VARCHAR2(15) NULL,
                                email VARCHAR2(50) NULL,
                                username VARCHAR2(20) NOT NULL,
                                password VARCHAR2(120) NOT NULL,
                                status NUMBER(1) NOT NULL,
                                created_date TIMESTAMP(6) NOT NULL,
                                modified_by VARCHAR2(64) NULL,
                                CONSTRAINT pk_users PRIMARY KEY (id),
                                CONSTRAINT uk_username UNIQUE (username),
                                CONSTRAINT uk_email UNIQUE (email),
                                CONSTRAINT uk_phone UNIQUE (phone)
);
GRANT ALL PRIVILEGES ON UTC_JAVA.USERS TO UTC_JAVA;
-- Tạo bảng user_roles
CREATE TABLE UTC_JAVA.users_roles (
                                      user_id NUMBER(19) NOT NULL,
                                      role_id NUMBER(10) NOT NULL,
                                      PRIMARY KEY (user_id, role_id),
                                      CONSTRAINT fk_user_roles_users FOREIGN KEY (user_id) REFERENCES UTC_JAVA.users (id) ON DELETE CASCADE,
                                      CONSTRAINT fk_user_roles_roles FOREIGN KEY (role_id) REFERENCES UTC_JAVA.roles (id) ON DELETE CASCADE
);
GRANT ALL PRIVILEGES ON UTC_JAVA.USERS_ROLES TO UTC_JAVA;
-- Tạo bảng categories
CREATE TABLE UTC_JAVA.categories (
                                     id NUMBER(19) GENERATED BY DEFAULT AS IDENTITY,
                                     user_id NUMBER(19) NOT NULL,
                                     name VARCHAR2(64) NOT NULL,
                                     status NUMBER(1) NOT NULL,
                                     created_date DATE NOT NULL,
                                     modified_by VARCHAR2(64) NOT NULL,
                                     PRIMARY KEY (id),
                                     CONSTRAINT fk_categories_users FOREIGN KEY (user_id) REFERENCES UTC_JAVA.users (id) ON DELETE CASCADE
);
GRANT ALL PRIVILEGES ON UTC_JAVA.CATEGORIES TO UTC_JAVA;

-- Tạo bảng news
CREATE TABLE UTC_JAVA.news (
                               id NUMBER(19) GENERATED BY DEFAULT AS IDENTITY,
                               user_id NUMBER(19) NOT NULL,
                               title VARCHAR2(128) NOT NULL,
                               content CLOB NOT NULL,
                               status NUMBER(1) NOT NULL,
                               created_date DATE NOT NULL,
                               modified_by VARCHAR2(64) NOT NULL,
                               PRIMARY KEY (id),
                               CONSTRAINT fk_news_users FOREIGN KEY (user_id) REFERENCES UTC_JAVA.users (id) ON DELETE CASCADE
);
GRANT ALL PRIVILEGES ON UTC_JAVA.NEWS TO UTC_JAVA;


-- Tạo bảng products
CREATE TABLE UTC_JAVA.products (
                                   id NUMBER(19) GENERATED BY DEFAULT AS IDENTITY,
                                   user_id NUMBER(19) NOT NULL,
                                   title VARCHAR2(128) NOT NULL,
                                   content CLOB NOT NULL,
                                   image VARCHAR2(255) NOT NULL,
                                   price NUMBER(19) NOT NULL,
                                   amount NUMBER(19) NOT NULL,
                                   unit VARCHAR2(100) NOT NULL,
                                   status NUMBER(1) NOT NULL,
                                   created_date DATE NOT NULL,
                                   modified_by VARCHAR2(64) NOT NULL,
                                   PRIMARY KEY (id),
                                   CONSTRAINT fk_user FOREIGN KEY (user_id) REFERENCES UTC_JAVA.users (id) ON DELETE CASCADE
);

GRANT ALL PRIVILEGES ON UTC_JAVA.PRODUCTS TO UTC_JAVA;

-- Tạo bảng products_categories
CREATE TABLE UTC_JAVA.products_categories (
                                              product_id NUMBER(19) NOT NULL,
                                              category_id NUMBER(19) NOT NULL,
                                              PRIMARY KEY (category_id, product_id),
                                              CONSTRAINT fk_category_id FOREIGN KEY (category_id) REFERENCES UTC_JAVA.categories (id) ON DELETE CASCADE ,
                                              CONSTRAINT fk_product_id FOREIGN KEY (product_id) REFERENCES UTC_JAVA.products (id) ON DELETE CASCADE
);
GRANT ALL PRIVILEGES ON UTC_JAVA.PRODUCTS_CATEGORIES TO UTC_JAVA;

-- Tạo bảng orders
CREATE TABLE UTC_JAVA.orders (
                                 id NUMBER(19) GENERATED BY DEFAULT AS IDENTITY,
                                 user_id NUMBER(19) NOT NULL,
                                 total_amount NUMBER(10) NOT NULL,
                                 address VARCHAR2(255) NOT NULL,
                                 phone VARCHAR2(15) NOT NULL,
                                 note VARCHAR2(255),
                                 status VARCHAR2(255) NOT NULL,
                                 created_date DATE NOT NULL,
                                 modified_by VARCHAR2(64) NOT NULL,
                                 PRIMARY KEY (id),
                                 CONSTRAINT fk_orders_users FOREIGN KEY (user_id) REFERENCES UTC_JAVA.users (id) ON DELETE CASCADE
);
GRANT ALL PRIVILEGES ON UTC_JAVA.orders TO UTC_JAVA;
-- Tạo bảng order_detail
CREATE TABLE UTC_JAVA.order_detail (
                                       id NUMBER(19) GENERATED BY DEFAULT AS IDENTITY,
                                       order_id NUMBER(19) NOT NULL,
                                       product_id NUMBER(19) NOT NULL,
                                       quantity NUMBER(10) NOT NULL,
                                       created_date DATE NOT NULL,
                                       modified_by VARCHAR2(64) NOT NULL,
                                       PRIMARY KEY (id),
                                       CONSTRAINT fk_order_detail_products FOREIGN KEY (product_id) REFERENCES UTC_JAVA.products (id) ON DELETE CASCADE  ,
                                       CONSTRAINT fk_orders_order_detail FOREIGN KEY (order_id) REFERENCES UTC_JAVA.orders (id) ON DELETE CASCADE
);
GRANT ALL PRIVILEGES ON UTC_JAVA.order_detail TO UTC_JAVA;






